const ResultWindow = ({ resObj }) => {

    const convertToFarenheit = (temp) => {
        const converted = (((temp-273.15) * 1.8) + 32)
        return converted
    }

    return (
        <div>
            <h3>Result Window</h3>
            <p><strong>Current Temperature</strong>{Math.trunc(convertToFarenheit(resObj.data.main.temp))}&deg;</p>
        </div>
    )
}

export default ResultWindow
