import { useState, useEffect } from 'react'
import axios from 'axios'

import ResultWindow from '../components/ResultWindow'

const Index = () => {

    const [text, setText] = useState('')
    const [city, setCity] = useState('')
    // store response object in the state
    const [resObj] = useState({})
    const API_KEY = '76cc49612d36cbf886e0abbde5395871'

    useEffect(() => {
        if(!city || !city.length) {
            return
        }

        submitCity()
    }, [city])

    const handleChange = (e) => {
        e.preventDefault()

        setText(e.target.value)
    }

    const submitCity = () => {
        axios.get(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}`)
            .then(res => {
                console.log(res)
            })
            .catch(err => console.log(err))
        setText('')
    }

    return (
        <div>
            <form>
                <input value={text} onChange={handleChange} type="text" />
                <button onClick={((e) => {
                    e.preventDefault()
                    setCity(text)
                })}>Submit</button>
            </form>
            {
                city.length >= 1 ? <ResultWindow /> : ''
            }
        </div>
    )
}

export default Index
