import { useState, useEffect } from 'react'

const API_KEY = '76cc49612d36cbf886e0abbde5395871'

const Index = () => {

    const [text, setText] = useState('')
    const [city, setCity] = useState('')

    useEffect(() => {
        if(!city.length) {
            return
        }

        submitCity()
    })

    const handleChange = (e) => {
        e.preventDefault()

        setText(e.target.value)
    }

    const submitCity = () => {
        console.log(city)
        setText('')
    }

    return (
        <div>
            <form>
                <input value={text} onChange={handleChange} type="text" />
                <button onClick={((e) => {
                    e.preventDefault()
                    setCity(text)
                })}>Submit</button>
            </form>
        </div>
    )
}

export default Index
